package seleniumTraining;

public class MathematicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println(10/2);
		System.out.println(9/2);
		System.out.println(9/2.0);
		System.out.println(9.0/2);
		System.out.println(9.0/2.0);
		//System.out.println(9/0);//Arthematic exeption
		System.out.println(0/9);
		//System.out.println(0/0);//Arthematic exception
		System.out.println(9.0/0);
		System.out.println(9/0.0);
		System.out.println(9.0/0.0);
		System.out.println(0/0.0);
		System.out.println(0.0/0.0);
		System.out.println(10.3/0);
		System.out.println(10/0.0);
		System.out.println('a'/2);
		//System.out.println('a'/0);//Arthematic exception
		System.out.println('a'/2+100);
		System.out.println(10%2);
		System.out.println(9%2);
		System.out.println(100%3);
		System.out.println(10%3);
		
		
		
		
		
	}

}
